// Fork of "Brachistochrone" by wyatt. https://shadertoy.com/view/7tfGD2
// 2021-06-07 04:54:05

Main {
    float 
        n = A(U+vec2(0,1)).z,
        e = A(U+vec2(1,0)).z,
        s = A(U-vec2(0,1)).z,
        w = A(U-vec2(1,0)).z;
    vec3 g = .2+normalize(vec3(e-w,n-s,4));
    Q = g.x*vec4(1,.5,0,1)+g.y*vec4(0,.5,1,1);

}

Main {
    float v = .001*length(B(U).xyz);
    v *= v;
    Q = A(U);
    for (int x =-2;x<=2;x++)
    for (int y =-2;y<=2;y++)
    if (x!=0||y!=0)
    {
        vec2 u = vec2(x,y);

        float uv = .001*length(B(U+u).xyz);
        uv *= uv;
        float dx_dt = .5*uv+.5*v;
        vec4 a = A(U+u);
        float dx = length(u);
        float dt = dx/dx_dt;
        if (abs(dx_dt)<1e-9) dt = 1e9;
        if (a.z+dt<Q.z)
            Q.z = a.z+dt;

        if (length(a.xy-U)<length(Q.xy-U))
            Q.xy = a.xy;
    }
    if (iMouse.z>0.&&length(U-iMouse.xy)<2.)
        Q.z = 0.;
    if (U.x<1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.) {
        Q = vec4(0);
    }
    if (iFrame < 1) {
        Q = vec4(0);
        Q.z = 1e9;
    }
}
