#define MAX_STEPS 100
#define MAX_DIST 10.0
#define SURF_DIST 0.001


mat2 Rot(float a){
    float s=sin(a),c=cos(a);
    return mat2(c,-s,s,c);
}

float octahedron(vec3 p,float s){
    p.xz*=Rot(iTime);
	p=abs(p);
	float m=p.x+p.y+p.z-s;
	vec3 r=3.0*p-m;

	vec3 q;
	if(r.x<0.0)q=p.xyz;
	else if(r.y<0.0)q=p.yzx;
	else if(r.z<0.0)q=p.zxy;
	else return m*0.57735027;

	float k=clamp(0.5*(q.z-q.y+s),0.0,s);
	return length(vec3(q.x,q.y-s+k,q.z-k));
}

float GetDist(vec3 p){
    float d=octahedron(p,1.5);
    return d;
}

float RayMarch(vec3 ro,vec3 rd,float side){
    float dO=0.0;

    for(int i=0;i<MAX_STEPS;i++){
        vec3 p=ro+rd*dO;
        float dS=GetDist(p)*side;
        dO+=dS;
        if(dO>MAX_DIST||abs(dS)<SURF_DIST)break;
      }
      return dO;
}

vec3 GetNormal(vec3 p){
        float d=GetDist(p);
        vec2 e=vec2(0.001,0.0);

        vec3 n=d-vec3(
            GetDist(p-e.xyy),
            GetDist(p-e.yxy),
            GetDist(p-e.yyx));

        return normalize(n);
}

vec3 GetRayDir(vec2 uv,vec3 p,vec3 l,float z){
    vec3 f=normalize(l-p),
    r=normalize(cross(vec3(0.0,1.0,0.0),f)),
    u=cross(f,r),
    c=f*z,
    i=c+uv.x*r+uv.y*u,
    d=normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 m=iMouse.xy/iResolution.xy;

    vec3 ro=vec3(0.0,3.0,-3.0);
    ro.yz*=Rot(-m.y*3.14+1.0);
    ro.xz*=Rot(-m.x*6.2831);


    vec3 rd=GetRayDir(uv,ro,vec3(0.0),1.0);
    vec3 col=texture(iChannel0,rd).rgb;

    float d=RayMarch(ro,rd,1.0);

    float IOR=1.45;

    if(d<MAX_DIST){
        vec3 p=ro+rd*d;
        vec3 n=GetNormal(p);
        vec3 r=reflect(rd,n);

        vec3 rdIn=refract(rd,n,1.0/IOR);

        vec3 pEnter=p-n*SURF_DIST*3.0;
        float dIn=RayMarch(pEnter,rdIn,-1.0);

        vec3 pExit=pEnter+rdIn*dIn;
        vec3 nExit=-GetNormal(pExit);

        vec3 rdOut=refract(rdIn,nExit,IOR);
        if(dot(rdOut,rdOut)==0.0)rdOut=reflect(rdIn,nExit);

        vec3 refTex=texture(iChannel0,rdOut).rgb;
        col=vec3(refTex);
    }

    col=pow(col,vec3(0.4545));

    fragColor = vec4(col,1.0);
}

#define hue(v) (0.6+0.6*cos(6.3*(v)+vec4(0.0,23.0,21.0,0.0)))

void mainCubemap( out vec4 fragColor, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir )
{
    vec3 A=abs(rayDir);
    int i=0;
    float M=A.x;
    if(A.y>M)M=A.y,i=1;
    if(A.z>M)M=A.z,i=2;
    int faceID=i+3*int(rayDir[i]<0.0);

    fragColor=hue(float(faceID)/6.);

    fragCoord=(fragCoord+fragCoord-iResolution.xy)/iResolution.y;
    fragColor*=vec4(0.2*1.0/length(fragCoord));
}
