void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 st = uv-.5;
    float d = length(st);

    vec3 col = vec3(0);

    vec2 offs = st*.02*d;
    col.r += texture(iChannel0, uv-offs).r;
    col.g += texture(iChannel0, uv).r;
    col.b += texture(iChannel0, uv+offs).r;

    //col *= 0.;
    //col += texture(iChannel0, uv).rgb;

    //col = mix(col, vec3(1,0,1), Timing(fragCoord));

    fragColor = vec4(col,1.0);
}

// "Truchet Trip #1" 
// by Martijn Steinrucken aka BigWings/The Art of Code - 2021
//
// Finally decided to test the NFT waters. Let's see what I can learn!
//
// If you are interested in owning this as an NFT, check this link:
// https://app.nafter.io/detail-post/6169c7cea55a4583cf361032

#define S smoothstep
#define TAU 6.283185

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float Hash21(vec2 p) {
    p = fract(p*vec2(123.3,234.3));
    p += dot(p, p+23.2);
    return fract(p.x*p.y);
}

float Length(vec2 p, float k) {

    return pow(pow(abs(p.x), k)+pow(abs(p.y), k), 1./k);
}

vec3 Tile(vec2 uv, float level, float n, float k) {
    float maxBlur = (1.+level*.1)*50./iResolution.y;//.05/level;

    float lev = 8./level;

    if(n<0.5) uv.x *= -1.;
    if(fract(n*10.)<0.5) uv.y *= -1.;


    uv +=.5; 

    float m, w;
    w = maxBlur;//min(.1, 2.*fwidth(uv.y*lev));

    if(fract(n*102.3)<.2*level) {
        if(n<.5) uv = uv.yx;
        w = maxBlur;//min(.1, 2.*fwidth(uv.y*lev));
        m = S(w, -w, abs(fract(uv.y*lev)-.5)-.25);
    } else {
        float d1 = Length(uv, k);
       // w = maxBlur;//min(fwidth(d1)*5., maxBlur);

        float c1 = S(.01, -.01, d1-1.);
        float t1 = c1*S(w, -w, abs(fract(d1*lev)-.5)-.25);

        float d2 = Length(uv-1., k);
        float w2 = w;//min(fwidth(d2)*5., maxBlur);

        float c2 = S(.01, -.01, d2-1.);
        float t2 = c2*S(w2, -w2, abs(fract(d2*lev)-.5)-.25);

        m = mix(t1, t2, S(.025, -.025, d2-(1.-(level-1.)*.03)));
    }

    return m*vec3(1);
}

vec2 sabs(vec2 p, float k) {
    return sqrt(p*p+k);
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 M = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    uv *= 5.;

    float k = .001;

    float cd = length(uv);
    float t = M.x*16.;
    t = mod(iTime, 16.);
    uv *= Rot(t*TAU/16.+sin(t*TAU/8.)*cd*.25);
    uv = vec2(8.*(atan(uv.x, uv.y)/6.2832+.5), log2(cd)-t);

    vec2 gv = fract(uv)-.5;
    vec2 id = mod( floor(uv), 16.);

    float n = Hash21(id);

    float shape = 2.;
    float checker = floor(mod(id.x+id.y, 2.));
    col = Tile(gv, 1., n, shape);
    //col *= 0.; col += checker;
    //if(gv.x>.48 || gv.y>.48) col = vec3(1,0,0);

    if(fract(n*25.)<.25) {
        vec2 gv2 = fract(gv*2.)-.5;
        vec2 id2 = floor(gv*2.);
        float n2 = Hash21(id2+id);

        col = Tile(gv2, 2., n2, shape);
        //if(gv2.x>.48 || gv2.y>.48) col = vec3(0,1,0);

        if(fract(n2*25.)<.25) {
            vec2 gv3 = fract(gv2*2.)-.5;
            vec2 id3 = floor(gv2*2.);
            float n3 = Hash21(id+id2+id3);

            col = Tile(gv3, 4., n3, shape);
           // if(gv3.x>.48 || gv3.y>.48) col = vec3(0,0,1);

            if(fract(n3*25.)<.25) {
                vec2 gv4 = fract(gv3*2.)-.5;
                vec2 id4 = floor(gv3*2.);
                float n4 = Hash21(id+id2+id3+id4);

                col = Tile(gv4, 8., n4, shape);
                //if(gv4.x>.48 || gv4.y>.48) col = vec3(1,0,1);
            }  
        }    
    }

   col = mix(col, vec3(.5), S(.8, .05, cd));
    if(false) {
        col *= 0.; 
        uv *= .5;
        col += Tile(uv, 1., .8, 2.);

        if(uv.x<-.5 || uv.x>.5 || uv.y<-.5||uv.y>.5) col *= .1;//vec3(0,0,1);
    }

    fragColor = vec4(col,1.0);
}
