    float Circle(vec2 xy, float r, vec2 pos)
    {
        float d = length(xy-pos);
          float cl = smoothstep(r, r-0.0001, d);
          return cl;
    }

    float Circle2(vec2 uv, vec2 p, float r) {
    float d = length(uv - p);
    float c = smoothstep(r, r-0.0001, d);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy/iResolution.xy;
    xy -= 0.5;
      xy.x *= iResolution.x/iResolution.y;
      float krosh = Circle(xy, 0.23, vec2(0.0, -0.)); 
        krosh -= Circle(xy, 0.05, vec2(0.06, 0.04));
        krosh -= Circle(xy, 0.045, vec2(-0.06, 0.04));
        krosh -= Circle(xy, 0.025, vec2(0.0, -0.02));
        krosh += Circle(xy, 0.02, vec2(0.08, 0.04));
        krosh += Circle(xy, 0.018, vec2(-0.045, 0.04));
      xy.x /= iResolution.y/(2.0*iResolution.x);
        krosh = max(krosh, Circle(xy, 0.18, vec2(0.37, 0.26)));
        krosh = max(krosh, Circle(xy, 0.18, vec2(-0.37, 0.26)));
        krosh = max(krosh, Circle(xy, 0.11, vec2(0.82, -0.04)));
        krosh = max(krosh, Circle(xy, 0.11, vec2(-0.82, -0.04)));  
      xy.x /= 4.2*iResolution.x/iResolution.y;
        krosh = max(krosh, Circle(xy, 0.04, vec2(0.066, -0.2)));
        krosh = max(krosh, Circle(xy, 0.04, vec2(-0.066, -0.2)));


        vec2 uv = fragCoord / iResolution.xy;
        uv -= .5;
        uv.x *= iResolution.x / iResolution.y;
        float mouth = Circle2(uv, vec2(.0, -.04), .08);
            mouth -= Circle2(uv, vec2(.0, .0), .0999);
           //mouth += Circle2(uv, vec2(.0, .05), .0999);

        krosh-= mouth;



          vec3 pic = vec3(0., 0., 1.0)*krosh;
            fragColor = vec4(pic, 1.0);


}
