#define INFTY 99999.99

struct Ray { vec3 origin; vec3 direction; };

struct Sphere { vec3 center; float radius; vec3 color; float specular; };

#define POINT 1 
#define AMBIENT 2 
#define DIRECTIONAL 3

struct Light { int type; float intensity; vec3 vector;};

struct Scene { Sphere[4] spheres; Light[3] lights; vec3 bgcolor; };

struct Intersection { int obj_id; float t; };

struct Camera { vec3 position; };

float[2] intersectRaySphere(Ray ray, Sphere sphere) {
    float[2] ts = float[2](INFTY,INFTY);
    float r = sphere.radius;
    vec3 CO = ray.origin - sphere.center;
    float a = dot(ray.direction, ray.direction);
    float b = 2.0f * dot(CO, ray.direction);
    float c = dot(CO, CO) - r * r;
    float D = b * b - 4.0f * a * c;
    if (D < 0.0f) return ts;
    ts[0] = (-b + sqrt(D)) / (2.0f * a);
    ts[1] = (-b - sqrt(D)) / (2.0f * a);
    return ts;
}

Intersection closestIntersection(Scene scene, Ray ray, float t_min, float t_max) {
    float closest_t = INFTY;
    int closest_sphere_id = -1;
    for (int i = 0; i < scene.spheres.length(); i++) {
        float[2] ts = intersectRaySphere(ray, scene.spheres[i]);
        for (int k = 0; k < 2; k++) {
            if (t_min < ts[k] && ts[k] < t_max && ts[k] < closest_t) {
                closest_t = ts[k];
                closest_sphere_id = i;
            }
        }
    }
    return Intersection(closest_sphere_id, closest_t);
}

float lightIntensity(vec3 P, vec3 N, Scene scene, vec3 V, float s) {
    float its = 0.0;
    vec3 L = vec3(0,0,0);
    float t_max;
    for (int i = 0; i < scene.lights.length(); i++) {
        Light light = scene.lights[i];
        if (light.type == AMBIENT) {
            its += light.intensity;
        } else {
            if (light.type == POINT) {
                L = light.vector - P;
                t_max = 1.0f;
            } else {
                L = light.vector; 
                t_max = INFTY;
            }

            // shadow check
            Intersection shadow = closestIntersection(scene, Ray(P, L), 0.001, t_max);
            if (shadow.obj_id != -1) {
               continue;
            }
            // diffuse
            float N_dot_L = dot(N,L);
            if (N_dot_L > 0.0) {
                its += light.intensity * N_dot_L / (length(N) * length(L));
            }

            // specular
            if (s != -1.0) {
                vec3 R = 2.0 * N * N_dot_L - L;
                float R_dot_V = dot(R, V);
                if (R_dot_V > 0.0f) {
                    its += light.intensity * pow((R_dot_V) / (length(R) * length(V)), s);
                }
            }
        }
    }

    return its;
}

vec3 traceRay(Scene scene, Ray ray, float t_min, float t_max) {
    Intersection inters = closestIntersection(scene, ray, t_min, t_max);

    if (inters.obj_id == -1) {
        return scene.bgcolor;
    }

    Sphere closest = scene.spheres[inters.obj_id];
    vec3 P = ray.origin + inters.t * ray.direction;
    vec3 N = P - closest.center;
    N = N / length(N);
    return closest.color * lightIntensity(P, N, scene, -ray.direction, closest.specular);
}

vec2 canvasToWorld(vec2 v) {
     vec2 uv = v / iResolution.xy - vec2(0.5);
     vec2 ab = uv * vec2((iResolution.x)/(iResolution.y), 1.0);
     return ab;
 }


float buzz(float amp) {
    return amp * cos(iTime);
}

vec3 getCoord() {
    return vec3(
    	texelFetch(iChannel0, ivec2(0, 0), 0).r,
        texelFetch(iChannel0, ivec2(1, 0), 0).r,
        texelFetch(iChannel0, ivec2(2, 0), 0).r
    );
}

Scene initScene() {
    Sphere sp1 = Sphere(vec3(0,-1.0 + buzz(1.0),3.0), 1.0f, vec3(0.7333, 0.1412, 0.1412), 500.0);
    Sphere sp2 = Sphere(vec3(2.0+buzz(0.3),0,4.0), 1.0f, vec3(0.6549, 0.6549, 0.9922), 500.0);
    Sphere sp3 = Sphere(vec3(-2.0+buzz(10.0),0.5,4.0), 0.5f, vec3(0.1843, 0.3333, 0.1843), 10.0);
    Sphere sp4 = Sphere(vec3(0,-5001,0), 5000.0f, vec3(0.8549, 0.8549, 0.8549), 1000.0);
    Light li1 = Light(AMBIENT, 0.2, vec3(0,0,0));
    Light li2 = Light(POINT, 0.6, vec3(2.0, 1.0, 0.0));
    Light li3 = Light(DIRECTIONAL, 0.2, vec3(1.0,4.0,4.0));
    return Scene(Sphere[4](sp1,sp2,sp3,sp4), 
               Light[3](li1, li2, li3),
               vec3(0.0275, 0.2039, 0.4314));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec3 abc = vec3(canvasToWorld(fragCoord), 1.0);
    Scene scene = initScene();
    Camera cam = Camera(vec3(getCoord()));

    vec2 mouse = canvasToWorld(iMouse.xy);

    Ray ray = Ray(cam.position, abc);
    vec3 col = traceRay(scene, ray, 1.0f, INFTY);
    fragColor = vec4(col,1.0);
}

// buffer A
const int KEY_LEFT  = 37;
const int KEY_RIGHT = 39;
const int KEY_UP    = 38;
const int KEY_DOWN  = 40;
const int KEY_A = 65; // move left
const int KEY_D = 68; // move right
const int KEY_W = 87; // move forward
const int KEY_S = 83; // move backward
const float speed = 10.0;

int[6] keys = int[6](KEY_D, KEY_A, KEY_UP, KEY_DOWN, KEY_W, KEY_S);

float keyPressed(int key) {
   return (iTimeDelta * speed) * texelFetch(iChannel1, ivec2(key, 0), 0).r;
}

float getKeyInput(int increase_key, int decrease_key) {
    return keyPressed(increase_key) - keyPressed(decrease_key);
}

float getPrevious(int register) {
    return texelFetch(iChannel0, ivec2(register, 0), 0).r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    int dir = int(fragCoord.x);
    float outData = getPrevious(dir) + getKeyInput(keys[2 * dir], keys[2*dir+1]);
    fragColor = vec4(outData, 0.0, 0.0, 1.0);
}
