// Modeling - 2022.02.06
// Eric Galin

const int Steps = 200;      // Number of steps
const float Epsilon = 0.01; // Marching epsilon


// Operators
// Union
// a,b : field function of left and right sub-trees
float Union(float a,float b)
{
    return min(a,b);
}

// Intersection
// a,b : field function of left and right sub-trees
float Intersection(float a,float b)
{
    return max(a,b);
}
float Intersection2(float a,float b)
{
    return min(max(a,-b),max(b,-a));
}
// Primitives
// Sphere
// p : point
// c : center of skeleton
// r : radius
float Sphere(vec3 p, vec3 c,float r)
{
  return length(p-c)-r;
}

// Sphere
// p : point
// n : Normal of plane
// o : Point on plane
float Plane(vec3 p, vec3 n, vec3 o)
{
    return dot((p-o),n);
}
//Capsule
float Capsule (vec3 f, vec3 g, vec3 p,vec3 o, float r) {
    vec3 q= p+o;
    vec3 a= f+o;
    vec3 b= g+o;
  float l = dot((p-a),(b-a))/length(b-a);
  float d;
  if (l<0.0) {
    d = length(p-a);
  } else if (0.0<l && l<length(b-a)) {
      d = sqrt (dot(p-a,p-a)-l*l);
  } else {
      d = length(p-b);
  }
  return d-r;
}
//Cylindre
float Cylindre(vec3 l, vec3 a , vec3 b ,vec3 c, float r){
    vec3 p=l+c;
    float dc=Capsule (a,b,p,vec3 (0.0),r);
    float f= Intersection ( dc, Plane ( p,(a-b)/length(b-a),a+vec3(0.0,0.0,0.0)) );
    float g= Intersection ( dc,Plane(p,(b-a)/length(b-a),b+vec3(0,0,0)) );
    return Intersection(f,g);
}



//Tore
float Tore (vec3 p, vec2 t,vec3 o) {
    vec3 c= p+o;
    vec2 q = vec2(length(c.xz)-t.x,c.y);
    return length(q)-t.y;
}

//Boite
float box (vec3 p, vec3 o, float r,float h) {
    return Intersection (
        Plane (p,vec3(1.0,0.0,0.0),o+vec3(r,0.0,0.0)),
        Intersection (
            Plane (p,vec3(-1.0,0.0,0.0),o+vec3(-r,0.0,0.0)),
            Intersection (
                Plane (p,vec3(0.0,1.0,0.0),o+vec3(0.0,r,0.0)),
                Intersection (
                    Plane (p,vec3(0.0,-1.0,0.0),o+vec3(0.0,-r,0.0)),
                    Intersection (
                        Plane (p,vec3(0.0,0.0,1.0),o+vec3(0.0,0.0,h)),
                        Plane (p,vec3(0.0,0.0,-1.0),o+vec3(0.0,0.0,-h)))))));
}
float tankquifaitpaspeur(float v,vec3 p)
{
 v = Union(v, Capsule (vec3 (0.0,0.0,0.0), vec3 (0.0,7.0,0.0),p,vec3 (6.0,-3.5,-3.0), 1.0));
 v = Union(v, Capsule (vec3 (0.0,0.0,0.0), vec3 (0.0,7.0,0.0),p,vec3 (-6.0,-3.5,-3.0), 1.0));
 v = Union (v,box (p, vec3(0.0,0.0,0.0),3.0,3.0));
 v = Union (v,box (p, vec3(-6.0,0.0,0.0),3.0,3.0)); 
 v = Union (v,Intersection( box (p, vec3(6.0,0.0,0.0),3.0,3.0),Plane(p,normalize( vec3(2.0,0.0,8.0)),vec3(15.0,0.0,0.0))));
 v = Union(Tore (p, vec2(1.5,0.5), vec3(-6.0,-3.5,3.0)), v);
 v = Union(Tore (p, vec2(1.5,0.5), vec3(6.0,-3.5,3.0)), v);
 v = Union(Tore (p, vec2(1.5,0.5), vec3(6.0,3.5,3.0)), v);
 v = Union(Tore (p, vec2(1.5,0.5), vec3(-6.0,3.5,3.0)), v);
 v = Union(v,  Cylindre( p, vec3(6.0,0.0,4.0), vec3(6.0,0.0,0.0), vec3(6.0,0.0,-3.0) , 3.0));//////////////////////////////////////////////////////////////////////////////////
 v = Union(v,  Cylindre( p, vec3(18.0,0.0,1.0), vec3(6.0,0.0,0.0), vec3(5.0,0.0,-6.0) , 1.0));
 v = Union(v, Sphere(p,vec3( 9.0, -1.5,-2.0),1.0));
 v = Union(v, Sphere(p,vec3( 9.0, 1.5,-2.0),1.0));
 v = Union(v, Sphere(p,vec3( 0.0, 0.0,6.5),3.0));
 v = Union(v, Sphere(p,vec3(15.0, 0.0,7.0),0.8));
     return v;
}
// Potential field of the object
// p : point
float object(vec3 p)
{
  float v = 10.0;
  v = Union(v, tankquifaitpaspeur(v,p));
  v = Union(v, Capsule (vec3 (0.0,0.0,0.0), vec3 (0.0,7.0,0.0),p,vec3 (0.0,15.0,0.0), 1.0));
  v = Union (v,box (p, vec3(-6.0,15.0,0.0),1.0,3.0)); 
  v = Union(v, Sphere(p,vec3(5.0, 15.0,0.0),0.8));
  v = Union(v,  Cylindre( p, vec3(6.0,0.0,4.0), vec3(6.0,0.0,0.0), vec3(-2.0,-15.0,0.0) , 1.0));

  return v;
}

// Analysis of the scalar field

// Calculate object normal
// p : point
vec3 ObjectNormal(vec3 p)
{
  const float eps = 0.001;
  vec3 n;
  float v = object(p);
  n.x = object( vec3(p.x+eps, p.y, p.z) ) - v;
  n.y = object( vec3(p.x, p.y+eps, p.z) ) - v;
  n.z = object( vec3(p.x, p.y, p.z+eps) ) - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, float e,out bool h,out int s)
{
  h = false;

  // Start at the origin
  float t=0.0;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = object(p);
    // Hit object
      if (v < 0.0)
      {
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,v);
      // Escape marched too far away
      if (t>e)
      {
          break;
      }
  }
  return t;
}

// Lighting

// Ambient occlusion
// p : Point
// n : Normal
// r : Radius for ambient occlusion
// a : Number of smaples
float AmbientOcclusion(vec3 p,vec3 n, float r, int a)
{
    if (a==0) { return 1.0; }

    float ao=0.0;

    for (int i=0;i<a;i++)
    {
         vec3 d = Cosine(581.123*float(i) , n);

        int s;
        bool h;
        float t=SphereTrace(p,d,r,h,s);
        if (!h) {ao+=1.0;}
    }

    ao/=float(a);
    return ao;
}

// Background color
// d : Ray direction
vec3 background(vec3 d)
{
  return mix(vec3(0.45,0.55,0.99),vec3(0.65,0.69,0.99), d.z*0.5+0.5);
}

// Shadowing
// p : Point
// n : Normal
// l : Light direction
float Shadow(vec3 p,vec3 n,vec3 l)
{
    bool h;
    int s;
    float t = SphereTrace(p+0.1*n,l,100.0,h,s);
    if (!h)
    {
      return 1.0;
    }
    return 0.0;
}

// Shading and lighting
// p : Point
// n : Normal at point
// e : Eye direction
vec3 Shade(vec3 p, vec3 n, vec3 e)
{
     // Point light
    const vec3 lp = vec3(5.0, 10.0, 25.0);

    // Light direction to point light
    vec3 l = normalize(lp - p);

    // Ambient color
    vec3 ambient = 0.25+0.25*background(n);

    // Ambient occlusion
    ambient += 0.15 * AmbientOcclusion(p+0.1*n,n,5.0,0) * vec3(1.0,1.0,1.0);

    // Shadow computation
    float shadow = Shadow(p,n,l);

    // Phong diffuse
    vec3 diffuse = 0.35  * clamp(dot(n, l),0.0,1.0) * vec3(1.0,1.0,1.0);

    // Specular
    vec3 r = reflect(e,n);
    vec3 specular = 0.15 * pow(clamp(dot(r,l),0.0,1.0),30.0) * vec3(1.0,1.0,1.0);
    vec3 c = ambient + shadow * (diffuse + specular);
    return c;
}

// Shading according to the number of steps in sphere tracing
// n : Number of steps
vec3 ShadeSteps(int n)
{
   float t = float(n)/(float(Steps-1));
   return 0.5+mix(vec3(0.05,0.05,0.5),vec3(0.65,0.39,0.65),t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/4.0;
    // Recompute pixel coordinates in sub-picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p = (-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip = true;
    }
       else
       {
           pip = false;
       }
   }
   return p;
}


// Image
void mainImage( out vec4 color, in vec2 pxy )
{
    // Picture in picture on
    bool pip=true;

   // Pixel
     vec2 pixel=Pip(pxy, pip);

    // Mouse
    vec2 m=iMouse.xy/iResolution.xy;

    // Camera
    vec3 ro,rd;
    Ray(m,pixel,ro,rd);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t = SphereTrace(ro, rd, 100.0,hit, s);

    // Position
    vec3 pt = ro + t * rd;

  // Shade background
  vec3 rgb = background(rd);

  if (hit)
  {
    // Compute normal
    vec3 n = ObjectNormal(pt);

    // Shade object with light
    rgb = Shade(pt, n, rd);
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if (pip==true)
  {
      rgb = ShadeSteps(s);
  }


  color=vec4(rgb, 1.0);
}

// Modeling
// Eric Galin

// Hashing function
// Returns a random number in [-1,1]
float Hash(float seed)
{
    return fract(sin(seed)*43758.5453 );
}

// Cosine direction 
// seed : Random seed
//    n : Normal
vec3 Cosine( float seed, vec3 n)
{
    float u = Hash( 78.233 + seed);
    float v = Hash( 10.873 + seed);

    // Method by fizzer: http://www.amietia.com/lambertnotangent.html
    float a = 6.2831853 * v;
    u = 2.0 * u - 1.0;
    return normalize( n + vec3(sqrt(1.0-u*u) * vec2(cos(a), sin(a)), u) );
}

// Rotation matrix around z axis
// a : Angle
mat3 Rz(float a)
{
    float sa = sin(a); float ca = cos(a); 
    return mat3(ca,sa,0.0,    -sa,ca,0.0,  0.0,0.0,1.0);
}

// Compute the ray
//      m : Mouse position
//      p : Pixel
// ro, rd : Ray origin and direction
void Ray(vec2 m, vec2 p,out vec3 ro,out vec3 rd)
{
    float a = 3.0*3.14*m.x;
   	float le = 3.5;

    // Origin
    ro=vec3(37.0,0.0,15.0);
    ro*=Rz(a); 

    // Target
    vec3 ta = vec3(0.0,0.0,1.0);

    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,0.0,1.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	rd = normalize( p.x*uu + p.y*vv + le*ww );
}
