//#define ortho
#define MODULUS
//#define COMPARE
//#define RAYMARCH
float volume(vec3 o,vec3 d, float t1, float t2){
    vec3 p1=(o+d*t1);vec3 p2=(o+d*t2);
#ifdef MODULUS
    float fx1=mod(p1.x,1.);
    float fx2=mod(p2.x,1.);
    float if_=(floor(o.x+t1*d.x)*0.5+fx1*fx1*0.5)/d.x-(floor(o.x+t2*d.x)*0.5+fx2*fx2*0.5)/d.x;

#else
    float if_=(o.x*t1+d.x*t1*t1*0.5)-(o.x*t2+d.x*t2*t2*0.5);
#endif
    float t=t2;
    float step_size=0.001;
    float density=0.;
    while(t<t1){
        t+=step_size;
#ifdef MODULUS
        density+=step_size*mod((o+d*t).x,1.);
#else
        density+=step_size*(o+d*t).x;
#endif
    }
#ifdef COMPARE
    return if_-density;
#else
#ifdef RAYMARCH
    return density;
#else
    return if_;
#endif
#endif
}
#define rot(d) mat2(cos(d),sin(d),-sin(d),cos(d))
void mainImage( out vec4 col, in vec2 co )
{
    col=vec4(0.6,0.4,0.2,1)*0.01;
    //col=vec4(0,0,0,1);
    vec2 uv = (co-iResolution.xy*0.5)/iResolution.x;
#ifndef ortho
    vec3 d=normalize(vec3(uv,-0.7));
    vec3 o=vec3(0,0,8);
#else
    vec3 d=vec3(0,0,-1);
    vec3 o=vec3(uv*10.0,8);
#endif
    o.xz*=rot(iTime);
    d.xz*=rot(iTime);
    o.xz*=rot(-4.*iMouse.x/iResolution.x+0.5);
    d.xz*=rot(-4.*iMouse.x/iResolution.x+0.5);
    o.yz*=rot(-4.*iMouse.y/iResolution.y+0.5);
    d.yz*=rot(-4.*iMouse.y/iResolution.y+0.5);
    o.xz*=rot(-4.*iMouse.x/iResolution.x+0.5);
    d.xz*=rot(-4.*iMouse.x/iResolution.x+0.5);

    //box intersection from https://www.shadertoy.com/view/ld23DV
    vec3 m = 1.0/d;
    vec3 n = o/d;
    vec3 k = abs(m)*1.9;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );
    if( tN<tF&&tF>0.0) {
        float v=volume(o,d,tF,tN);
        col+=vec4(v)*0.2;
        col.r=abs(col.r);

    }
    col=sqrt(col);
}
