vec2 quadratic(float a, float b, float c){
    float s = sqrt((b * b) - (4. * a * c));
    float d = 2. * a;
    return vec2((-b + s) / d, (-b - s) / d);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 centerUV = uv - vec2(.5);

    vec4 col = vec4(0.);

    mat2 mInv = mat2(vec2(-1., cos(iTime)), vec2(-1., -1.));

    vec2 prev = -((mInv * centerUV) * iTimeDelta) + centerUV;

    if(iFrame == 0){

        col = (vec4(0., 0., 1., 0.) * sign(centerUV.x) * sign(centerUV.y)) + (vec4(1., 0., 0., 0.) * sign(centerUV.x)) + (vec4(0., 1., 0., 0.) * sign(centerUV.y));
    }
    else{
        col = texture(iChannel0, prev + vec2(.5));
    }

    fragColor = col;
}
