#define thresh 20.
#define stop 200
#define res iResolution

// from yiwenl
vec3 hsv2rgb(vec3 c){
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec2 cmul(vec2 a, vec2 b){
    return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 z = .8*(2.*fragCoord-res.xy)/res.y;  
    vec2 c = iMouse.xy==vec2(0)? vec2(-.77,.45)/*vec2(cos(iTime*.4)-.3,-.66)*/: (2.*iMouse.xy-res.xy)/res.y;
    int i = 0;
    for(i; i < stop; i++){   
        z = cmul(z,z)+c;   
        if(length(z) > thresh)
            break;      
    }
    vec3 col = hsv2rgb(vec3(log(.9+float(i)/380.)*2., 1., log(.7+float(i)/15.)));
    fragColor = vec4(col,1.0);
}
