float rectangleRow(vec2 vUv, float nRows, float select) {
    vec2 st = vUv;

    float rowFraction = select/nRows;
    float rowUnit = 1.0/nRows;
    st.xy*=nRows;
    st = fract(st);


    vec2 b = step( abs(st-.5), vec2(.4) ); 
    return float( b.x * b.y > 0.
                  && (nRows-select)/nRows <vUv.y
                  && vUv.y < 1.-rowFraction +rowUnit );


}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 vUv =fragCoord/iResolution.xy;

    float result = rectangleRow(vUv, 8.0, 2.0);

    float result2 = rectangleRow(vUv, 8.0, 6.0);


    fragColor = vec4(vec3(result+result2),1.0);
}
