void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x*=iResolution.x/iResolution.y;
    uv.x-=iResolution.x/iResolution.y/2.0;//画面の中心を0,0にして範囲を-1.0~1.0くらいにする
    uv.y-=0.1;//中心をずらす
    uv.y*=1.05;//縦につぶす

   float r = 0.6;//円の半径
   float y=sqrt(pow(r,2.0)-pow(uv.x,2.0));//円の式
   float col =((((sin(iTime)+(sin(iTime*0.7))/2.0+2.01)/100.0)/length(y-uv.y)));//関数


    fragColor = vec4(vec3(col),1.0);
}
