float Circle(vec2 xy,float r, vec2 position) 
    {
        float d = length(xy - position);
        float res = smoothstep(r, r - 0.006, d);
        return res;
    }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy/iResolution.xy;
    xy -= .5;
    xy.x *= iResolution.x/iResolution.y;
    float cheburashka = Circle(xy, 0.25, vec2(0.0, -0.1)); //тело
    cheburashka += Circle(xy, 0.15, vec2(0.0, 0.2)); //голова
    cheburashka += Circle(xy, 0.08, vec2(0.23, -0.02)); //рука
    cheburashka += Circle(xy, 0.08, vec2(-0.23, -0.02)); //рука
    cheburashka += Circle(xy, 0.1, vec2(0.2, 0.3)); //ухо
    cheburashka += Circle(xy, 0.1, vec2(-0.2, 0.3)); //ухо
    cheburashka += Circle(xy, 0.08, vec2(0.25, -0.3)); //нога
    cheburashka += Circle(xy, 0.08, vec2(-0.25, -0.3)); //нога


    vec3 pic = vec3(1.0, 1.0, 0.0)*cheburashka;
     fragColor = vec4(vec3(cheburashka), 0);
}
