float Circle(vec2 xy,float r, vec2 position) 
    {
        float d = length(xy - position);
        float res = smoothstep(r, r - 0.006 , d);
        return res;
    }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy/iResolution.xy;
    xy -= 0.5;
      xy.x *= iResolution.x/iResolution.y;
      float crosh  = Circle(xy, 0.23, vec2(0.0, -0.1)); //тело
        crosh -= Circle(xy, 0.035, vec2(0.06, -0.06)); //глаз
        crosh -= Circle(xy, 0.035, vec2(-0.06, -0.06)); //глаз
        crosh -= Circle(xy, 0.025, vec2(0.0, -0.12)); //нос


      xy.x /= iResolution.y/(2.0*iResolution.x);
        crosh = max(crosh, Circle(xy, 0.18, vec2(0.25, 0.28))); //ухо
        crosh = max(crosh, Circle(xy, 0.18, vec2(-0.25, 0.28))); //узо
        crosh = max(crosh, Circle(xy, 0.1, vec2(0.9, -0.1))); //рука
        crosh = max(crosh, Circle(xy, 0.1, vec2(-0.9, -0.1))); //рука  
      xy.x /= 5.0*iResolution.x/iResolution.y;
        crosh= max(crosh, Circle(xy, 0.05, vec2(0.08, -0.3))); //нога
        crosh = max(crosh, Circle(xy, 0.05, vec2(-0.08, -0.3))); //нога

          vec3 pic = vec3(0.4, 0.9, 1.0)*crosh;
            fragColor = vec4(pic, 1.0);
}
