void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 xy = fragCoord.xy / iResolution.xy;//Condensing this into one line

    vec2 px = vec2(1.0, 1.0) / iResolution.xy;
    int r = 4;
    float intens;
    float mid = 0.0;
    int it = 0;
    for (int i = -r; i <= r; ++i){
        for (int j = -r; j <= r; ++j){
            if (i * i + j * j <= r * r){
                ++it;
                vec4 cur = texture(iChannel0, xy + px.xy * vec2(i, j));
                intens = max(intens, (cur.x + cur.y + cur.z) / 3.0);
            }
        }
    }
    mid /= float(it);
    //vec4 texColor = texture(iChannel0, xy);//Get the pixel at xy from iChannel0
    fragColor = vec4(intens, intens, intens, 1.0);//Set the screen pixel to that color
}
