mat2 rotate(float theta){
    return mat2(cos(theta), -sin(theta),
              sin(theta), cos(theta));
}

float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(1.9898,78.233))) * 43758.5453123);
}
vec2 random2d(vec2 x) {
    return fract(sin(vec2(dot(x,vec2(127.1,311.7)),dot(x,vec2(269.5,183.3))))*43758.5453);
}

// Gradient Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/XdXGW8
float gradient_noise(vec2 x) {
    vec2 i = floor(x);
    vec2 f = fract(x);
    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2d(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2d(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2d(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2d(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y) * 1.5;// * 0.5 + 0.5;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy;
    vec3 color = vec3(0.0);

    float zoom = 5.0;
    uv *= zoom;
    uv *= rotate(gradient_noise(uv));

    vec2 uv_i = floor(uv);
    vec2 uv_f = fract(uv);

    float m_dist = 1.0;
    for (int j = -1; j < 2; ++j){
        for (int i = -1; i < 2; ++i){
            vec2 n = uv_i + vec2(float(i), float(j));
            vec2 pt = random2d(n);
            pt = 0.5 + 0.5*sin(iTime + 6.0 * pt);
            vec2 v = n + pt - uv;
            float d = length(v);
            m_dist = min(m_dist, d);
        }
    }

    color = vec3(m_dist + random(uv));
    //color = vec3(m_dist + random(vec2(0.0)));

    fragColor = vec4(color, 1.0);
}
