void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float x0 = (sin(iTime) + 1.0) / 2.0;
    float y0 = (cos(iTime) + 1.0) / 2.0;

    float x1 = (sin(-iTime) + 1.0) / 2.0;
    float y1 = (cos(-iTime) + 1.0) / 2.0;

    // Normalized pixel coordinates (from 0 to 1)
    float aspect = iResolution.x / iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;
    vec2 blob0 = vec2(x0, y0);
    vec2 blob1 = vec2(x1, y1);

    uv.y /= aspect;
    blob0.y /= aspect;
    blob1.y /= aspect;

    float sum = 0.0;

    sum += (1.0 / sqrt(pow(blob0.x - uv.x, 2.0) + pow(blob0.y - uv.y, 2.0))) * 0.05;
    sum += (1.0 / sqrt(pow(blob1.x - uv.x, 2.0) + pow(blob1.y - uv.y, 2.0))) * 0.05;

    // Output to screen
    vec3 color = vec3(sum - 1.5, sum - 3.0, sum);
    fragColor = vec4(color, 1.0);
}
