void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int thickness = 4;
    int vertical = 0;

    float val = gl_FragCoord.y;
    if (vertical == 1) {
        val = gl_FragCoord.x;
    }

    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    if (mod(val, 2.0 * float(thickness)) >= float(thickness)) {
        fragColor.g = 1.0;
    }
}
