#define R                           iResolution
#define TEX_COORDS(P)             ( P / R.xy )                       // in range x=[0.0; 1.0], y=[0.0; 1.0]
#define TEX3(x)                   ( texture(iChannel3, x) )
#define RIGHT_QUADRANT_NDC(SC)    ( (SC)/R.y )                       // in range x=[0.; AR], y=[0.0; 1.0]
#define NMX                       ( iMouse.x / R.x )                 // in range [0.0; 1.0]
#define LMB_IS_HOLD               ( iMouse.z > 0.0 )

vec4 char(
    vec2 P,
    int char_idx)  
{
	return 
        (P.x<.0 || P.x>1. || P.y<0.|| P.y>1.) == true ?
            vec4(0) :
            textureGrad( 
                iChannel3,
                fract( vec2(char_idx, 15 - (char_idx >> 4))*0.0625 ) + P*0.0625, 
                /* Top left corner?                                    Offset */
                dFdx(P*0.0625),
                dFdy(P*0.0625));
}

int get_char_idx(int x, int y) { return y * 16 + x; }

void mainImage( out vec4 O, in vec2 SC )
{
    //O = TEX3(TEX_COORDS(SC)); return; // font only;

    O = vec4(0);
    vec2 NDC = RIGHT_QUADRANT_NDC(SC);

    NDC *= 2.75 + mod(floor(iTime * 1.00), 16.); NDC += vec2(0.0, -0.75);

    O += char(NDC, get_char_idx(3, 5)).x;  NDC.x-=.5;  // S
    O += char(NDC, get_char_idx(8, 6)).x;  NDC.x-=.5;  // h
    O += char(NDC, get_char_idx(1, 6)).x;  NDC.x-=.5;  // a
    O += char(NDC, get_char_idx(4, 6)).x;  NDC.x-=.5;  // d
    O += char(NDC, get_char_idx(5, 6)).x;  NDC.x-=.5;  // e
    O += char(NDC, get_char_idx(2, 7)).x;  NDC.x-=.5;  // r
    O += char(NDC, get_char_idx(4, 7)).x;  NDC.x-=.5;  // t
    O += char(NDC, get_char_idx(15, 6)).x; NDC.x-=.5;  // o
    O += char(NDC, get_char_idx(9, 7)).x;  NDC.x-=.5;  // y

    float AA = 
        LMB_IS_HOLD == true ?
            1. * NMX :
            1.;

#define UNIT                      ( 4.0 / R.y )
#define SMAA(x)                   ( smoothstep(UNIT, 0., x) )
    O = smoothstep(0., AA, O);

    O = 1. - O;
}
