#define FXAA_SPAN_MAX 8.0
#define FXAA_REDUCE_MUL   (1.0/FXAA_SPAN_MAX)
#define FXAA_REDUCE_MIN   (1.0/128.0)
#define FXAA_SUBPIX_SHIFT (1.0/4.0)

float calcPosition(float val, float max, float min) {    
    return ((max*(val))-(min*(val))-(min * -1.0));
}

float rand(vec2 n) {
    return fract(cos(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 rcpFrame = 1./iResolution.xy;
  	vec2 uv2 = fragCoord.xy / iResolution.xy;

    float position = .5; // 0.0 - 1.0
    vec2 pos = fragCoord.xy/iResolution.yy;

    //set bg color
    fragColor = vec4(.0, .0, .0, 1.);

    float lineWidth = .4;
    float pointSize = .1;
    float lineWidthHalf = lineWidth / 2.;

    vec2 point1 = vec2(calcPosition(1. - position, -.3, 2.3), .8);
    vec2 point2 = vec2(calcPosition(1. - position, -.5, 2.2), .3);

    float m = (point1.y - point2.y) / (point1.x - point2.x);
    float c = point1.y - m*point1.x;

    vec2 line = vec2((pos.y-c)/m, pos.x*m+c);
    if ((pos.y > line.y-lineWidthHalf && pos.y < line.y+lineWidthHalf) || (pos.x > line.x-lineWidthHalf && pos.x < line.x+lineWidthHalf)) {
        fragColor = vec4(1.0, 1.0, .0, 1.);
    }

    //vec4 uv = vec4(uv2, uv2 - (rcpFrame * (0.5 + FXAA_SUBPIX_SHIFT)));
