// Fork of "ngwavesmod" by sleeplessmonk. https://shadertoy.com/view/7tdXRj
// 2021-12-13 19:31:17

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
// Calculate the current time
float t = iTime*1.54159;


// Calculate the UV coordinates for the current pixel
fragCoord -= iResolution.xy * 0.5;
vec2 uv = fragCoord / iResolution.y;
uv *= 2.;

// Initialize the color variable
vec4 c = vec4(0.);

// Calculate the angle and distance from the center of the screen
float a = atan(uv.y, uv.x);
float d = length(uv);

// Calculate the values for the smoothstep function
float s1 = -.05;
float s2 = s1 + (sin(t - d * 6.) * .1 + .15);

// Use a for loop to calculate the value of f
float f = 0.;
for(float i = 0.; i < 8.28; i += 6.28 / 128.)
{
    f += smoothstep(s1, s2, sin(a * (3.) - t + sin(i - t) * 2.5) * .1 * d + (d - i * .4));
}

// Add the time value to f
f += t;

// Use the sin function to create psychedelic colors
float spread = sin(t) * 0.4;
c.rgb = sin((vec3(f - spread, f, f + spread)) * 66.) * 0.5 + 0.5;

// Set the final color of the pixel
fragColor = c;
}
