#define RED vec4(244.,60.,50.,1.)/255.
#define BLUE vec4(0.,20.,137.,1.)/255.
#define GREEN vec4(0.,119.,73.,1.)/255.
#define YELLOW vec4(254.,184.,28.,1.)/255.
#define WHITE vec4(1.)


float yMask(vec2 uv, float tb, float lr, float w, float a){   
    // tb - up - down
    // lr - right left
    // w - width
    // a -angle of Y
    uv.y = abs(uv.y);
    return 1.-max(1.-(step(uv.y+w/2.,tb) + step(uv.x + uv.y*a, lr)), 
    (step(uv.y,tb-w) + step(uv.x + uv.y*a, lr-w)));       ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec4 col = vec4(0.);


    float yWhite = yMask(uv, 0.25,  0.2, 0.1, 1.5);
    col = mix(col, WHITE, yWhite);

    float yGreen = yMask(uv, 0.35, 0.13, 0.4, 1.5);
    col = mix(col,GREEN,yGreen);

    float yYellow = yMask(uv, 0., -0.2, 0.1, 1.5);
    col = mix(col,YELLOW,yYellow);

    float yBlue = yMask(uv, 0.9,  0.9, 0.7, 1.5);
    col = mix(col, RED,   clamp(ceil(uv.y)*yBlue,0.,1.));
    col = mix(col, BLUE,  clamp(ceil(-uv.y)*yBlue,0.,1.));


    fragColor = vec4(col);
}
