void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy; 

    vec3 col = texture(iChannel0, uv).rgb;

    fragColor = vec4(col, 1.0);
}

int GetNeighbours(ivec2 p){
    int num = 0;
    for (int y = -1; y <= 1; y++){
        for (int x = -1; x <= 1; x++){
            if (x==0 && y ==0) continue;
            num += texelFetch(iChannel1, p + ivec2(x,y), 0).r > 0.5 ? 1 : 0;
        }
    }
    return num;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy; 
    vec4 col = vec4(0);

    if ( iMouse.z > 0.0) {
        col = texture(iChannel0, uv);
    } else {
        bool alive = texelFetch(iChannel1, ivec2(fragCoord), 0).r > 0.5;
        int num = GetNeighbours(ivec2(fragCoord));

        int next = 0;
        if (alive && (num == 2 || num == 3))
            next = 1;
        else if (!alive && num == 3)
            next = 1;
        else 
            next = 0;

        col = vec4(next);

    }
    fragColor = col;
}
