void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = -1. + 2. * gl_FragCoord.xy / iResolution.xy;
    float aspect = iResolution.x/iResolution.y;

    uv.x *= aspect;

    float distance  = sin(0.5+sin(uv.x*sin(iTime/100.)*100.)+iTime) - length(uv);

    vec3 color = vec3(smoothstep(0.0, 0.01, distance));
    color *= vec3(smoothstep(-0.05,-0.04,-distance));

    fragColor = vec4(
        color,
        1.0);
}
