float PI = 3.14,
      R = .3,
      n = 14.;

void mainImage( out vec4 O, in vec2 u )
{
    vec2 Res = iResolution.xy, 
        U = ( 2. * u - Res ) / Res.y;   
    O-=O;
    int m = int(mod(iTime/4. * n, n) + 1.);

    float scale = 2.;
    float r = length(U);
    vec3 star = vec3(min(exp((-r + R)/.05), 1.) * abs(1./scale));

    float Rd = R * scale;
    for(float i = 0.; i < n; i++) {
        float a = float(i) * PI/(float(n)/2.) + iTime * iTime;
        r = distance(U, Rd * vec2(-sin(a), cos(a)));
        O += vec4(min(exp((-r + R)/.02), 1.)) * 0.2 * scale;
    }

    O+= vec4(star, 1.);
}
