vec3 color(int c) {
    float d = mod(float(c), 180.);
    float f = mod(d, 60.) / 60.;
    if(d < 60.)  return vec3(f, f, f);
    if(d < 120.) return vec3(1.-f, 1.-f, 1.);
    if(d < 180.) return vec3(0., 0., 1.-f);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float w = iResolution.x;
    float h = iResolution.y;
    float scale = min(iResolution.x, iResolution.y)/3.;
    if(fragCoord.x*2. > w) {
        float x = (fragCoord.x-3.*w/4.)/scale;
        float y = (fragCoord.y-h/2.)/scale;
        float cx = float(iMouse.x-w/4.) / scale - 0.7;
        float cy = float(iMouse.y-h/2.) / scale;
        for(int i = 0; i < 512; i++) {
            if(x*x + y*y >= 256.) {
                fragColor = vec4(color(i), 1.);
                return;
            }
            float nx = x*x - y*y + cx;
            float ny = 2.* x * y + cy;
            x = nx;
            y = ny;
        }
        fragColor = vec4(0., 0., 0., 1.);
        return;
    }
    if(pow(iMouse.x-fragCoord.x,2.)+pow(iMouse.y-fragCoord.y,2.) <= 4.) {
        fragColor = vec4(1., 0., 0., 1.);
        return;
    }
    float cx = (fragCoord.x-w/4.)/scale-0.7;
    float cy = (fragCoord.y-h/2.)/scale;
    float x = 0.;
    float y = 0.;
    for(int i = 0; i < 64; i++) {
        if(x*x + y*y >= 8.) {
            fragColor = vec4(color(i), 1.);
            return;
        }
        float nx = x*x - y*y + cx;
        float ny = 2.* x * y + cy;
        x = nx;
        y = ny;
    }
    fragColor = vec4(0., 0., 0., 1.);
}
