float random(vec2 point, float seed) {
    return fract(sin(point.x * 431. + point.y * 171.) * seed);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float randomX = random(uv, 5439.) * 1000.;
    if(randomX < 1.){
         float t = random(vec2(iDate.z * randomX, iDate.z * randomX), 3293.);
         t = t * step(0.,sin((uv.y + uv.x) * (iDate.w/10.)  ));
         fragColor = vec4(vec3(t),1.0);
     }


}
