#define N_ITER 50

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    float ar = iResolution.x/iResolution.y;
    uv -= vec2(.5*ar, .5);
    uv *= 3.;

    float t = .3*iTime;

    vec2 z = uv;
    vec2 z0 = .8*vec2(cos(t), sin(t));//vec2(0., 1.); // Change for diff julia sets
    int n = N_ITER;
    float xtemp = 0.;
    for (int i = 0; i < N_ITER; ++i) {
        xtemp = z.x*z.x - z.y*z.y + z0.x;
        z.y = 2.*z.y*z.x + z0.y;
        z.x = xtemp;
        if (length(z) > 2.) 
        {
            n = min(n, i);
        }
    }

    float c = mix(0., 1., float(n)/float(N_ITER));
    //c = pow(c, 2.);
    vec3 col = vec3(c);
    fragColor = vec4(col,1.0);
}
