#define S smoothstep
#define P 3.14159265
#define HASHSCALE1 443.8975
const vec3 orange = vec3(0.8509803921568627, 0.35294117647058826, 0.1843137254901961);
const vec3 blue = 1.-vec3(0.9509803921568627, 0.25294117647058826, 0.00843137254901961);

float hash13(vec3 p3)
{
	p3  = fract(p3 * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 irri(float hue) {
  return .5+ .5 *cos(( 9.*hue)+ vec3(0,23.,21.));
}

mat2 r(float a){
    return mat2(cos(a),-sin(a),
                sin(a),cos(a));
}
float m (vec2 u, float t, float o) {
    u *= r(t);
    return S(-P,P,sin(atan(u.x, u.y)*(0.5+o*.125))*u.y/u.x) * S(-0.125, 1.25, 1. - length(u*0.85));
}

float n (vec2 u, float t) {
    u *= r(t);
    return S(-P,P,atan(u.x, u.y)) * S(0.45, 1., 1. - length(u));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    float t = iTime * .5;
    vec2 uv = fragCoord/R;
    vec2 u = (fragCoord-.5*R)/R.y;
    float r = .025;
    float num = 11.;
    // weird display bug where if i multiply r * n, it get all pixelated
    float start = r+r+r+r+r;
    vec3 C = mix(blue,orange,sin(t+length(u))*.5+.5)*.5;
    float no = hash13(vec3(u,t*0.000001))*0.5;
    for(float i = 0.; i<num; i++){
        float s = i/(num)*P*2.;
        //vec2 o = vec2(-start + r*i, sin(t+s*8.)*.25);
        float b = sin(t+s)*no*0.0125; 
        vec2 o = vec2(cos(t+s*4.)*(.25+b), sin(t+s*4.)*(.25+b));
        float a = i/num*(2.*P/num);
        //float c = m(u+o,sin(-s+t/1.5)*P+P*(-.25 + s + s*.5));
        float c = m(u+o,no*0.125,no);
        C += (c * mix(orange,blue,pow(sin(s*32.+t)*.5+.5,1.25)));
    }
    C = C / pow(num/(num/2.15),1.45);
    C.x += (irri(C.z)*0.125).x;
    C.y += (irri(C.x)*0.005).y;
    C.z += (irri(C.y)*0.088).z;
    float sat = 0.125+ no * .125;
    fragColor = vec4(min(C*(1.+sat)-sat,vec3(1.)), 1.0);
    //fragColor = vec4(vec3(no),1.);
}
