#define PI 3.141592653
vec2 rotate2D (vec2 _uv, float _angle) {
    _uv -= 0.5;
    _uv =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _uv;
    _uv += 0.5;
    return _uv;
}

vec2 tile(vec2 _uv, float _zoom){
    _uv *= _zoom;
    return fract(_uv);
}

float circle(vec2 _st, float _radius){
    vec2 pos = vec2(0.5)-_st;
    return smoothstep(1.0-_radius,1.0-_radius+_radius*0.2,1.-dot(pos,pos)*3.14);
}

vec2 rotateTilePattern(vec2 _uv){
    _uv*=2.;
    float index =0.;
    index +=step(1.,mod(_uv.x,2.0));
    index +=step(1.,mod(_uv.y,2.0))*2.;

    _uv = fract(_uv);
    if(index ==1.){
        _uv = rotate2D(_uv,PI*0.5);
    } else if(index ==2.){
        _uv = rotate2D(_uv,PI*-0.5);
    } else if(index ==3.){
        _uv = rotate2D(_uv,PI);
    }



    return _uv;
}



void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv = tile(uv,3.);
    //uv = rotateTilePattern(uv);

    //uv = tile(uv,2.0);
    //uv = rotate2D(uv,-PI*iTime*0.25);
    uv = rotateTilePattern(uv*4.);
    uv = rotate2D(uv,PI*iTime*0.25);



    fragColor = vec4(vec3(step(uv.x,uv.y)),1.0);
}
