void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = vec2(uv.x*1.9-0.5, uv.y);
    float scale = abs(sin(iTime)-0.3)*cos(iTime);
    float h = texture(iChannel0, uv.yx).r;
    float h0 = texture(iChannel0, vec2(uv.y, sin(iTime))).r;
    scale = max(h0, h0*scale);

    // Time varying pixel color
    vec3 col = vec3(scale/length(uv-0.5))*vec3(0.7, 0.7, 0.9+sin(iTime)+0.4);
    col -= vec3(h);

    // Output to screen
    fragColor = vec4(col,1.0);
}
