vec3 image(vec2 uv, float time){
    //float middle = sin(time*2.0)/2.0f+.5f;
    //vec3 col = vec3(1,0,0);
    //if(uv.x > middle) col = vec3(0,0,1);
    //return col;

    uv.x += sin(time*2.0)/2.0f+.5f;

    return texture(iChannel0, uv).rgb;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	float time = iTime;
    float time2 = iTime - iTimeDelta;

    vec3 col1 = image(uv, time);
    vec3 col2 = image(uv, time2);

    //BLEND
    float lerpval = clamp((uv.y - .5) * 10.0, 0.0, 1.0);
    vec3 col = mix(col1, col2, lerpval);

    //TEAR
    //lerpval = 0;
    //if(uv.y > .5) lerpval = 1;


    // Output to screen
    fragColor = vec4(col,1.0);
}
