float random(in vec2 st)
{
	return fract(sin(dot(st.xy,vec2(12.443,78.4)))*43758.2);
}

float noise(in vec2 st)
{
	vec2 i=floor(st);
    vec2 f=fract(st);
    float a=random(i);
    float b=random(i+vec2(1.0,0.0));
    float c=random(i+vec2(0.0,1.0));
    float d=random(i+vec2(1.0,1.0));

    vec2 u=smoothstep(0.0,1.0,f);
    return mix(a,b,u.x)+(c-a)*u.y*(1.0-u.x)+(d-b)*u.x*u.y;

}

float square(vec2 p,float s)
{
	vec2 k=vec2(abs(p.x)+abs(p.y),0.5); 
    return smoothstep(0.1*s,0.1*s+0.02,k.x);
}

vec2 brickSquare(vec2 st,float n)
{
	st*=n;
    st*=noise(st)+sin(iTime*0.5);
    float s=step(1.,mod(st.y,2.))*0.5*iTime;
    st.x+=s;
    if(s==0.)
    {
   		st.x-=0.5*iTime;
    }
    return fract(st)-0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p=(fragCoord.xy*2.0-iResolution.xy)/min(iResolution.x,iResolution.y);
    float n=10.;
    p=brickSquare(p,n);
    float col=square(p,5.);
    fragColor=vec4(col);  
}
