void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    if (U.x < R.x/2.) U++; // offset coordinates by 1 pixel

    // MIPmap get discontinuous on one side.
    // Derivative discontinuity is hidden on the other.
    O = texture(iChannel0, fract(2.*U/R.y)).rrrr;

    // atan() is discontinuous on one side.
    // Derivative discontinuity is hidden on the other.
    U = 2.*U-R; U.x -= .25*R.y;
    float a = atan(U.y,U.x);
    if ( length(U)/R.y < .5 ) O = .5+5.*vec4(dFdx(a),dFdy(a),0,0);
}
