void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 l_color = texture(iChannel1, uv);
    vec4 f_color = texture(iChannel0, uv);
    f_color.rgb = f_color.rgb * l_color.a;
    fragColor = f_color;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord.xy/iResolution.xy;
    uv.x = uv.x * iResolution.x/iResolution.y * 0.75;
    vec2 mouse = iMouse.xy/iResolution.xy;
    mouse.x = mouse.x * iResolution.x/iResolution.y * 0.75;

    if(mouse.x == 0. && mouse.y == 0.){
        mouse.x = 0.27;
        mouse.y = 0.6;
    }

    float dist = distance(mouse, uv);
    float color = 0.1/dist;
    fragColor = vec4(color);
}
