highp float rand(vec2 co) {
    return fract(cos(mod(dot(co.xy ,vec2(12.9898,78.233)),3.14))*43758.5453);
}
highp float rand(float co) {
    return fract(cos(mod(co,3.14))*43758.5453);
}
float a(float p, float d){;
	return sign(fract(p*d*.5)*2.-1.);
}
const int iter=16;
const int n=1<<iter;
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y*1.;
	float u=float(n);
    float i=float(iter);
    float k=i;
    while(true){
		uv.y-=a(uv.x,u)/(u*2.)*clamp((1.-cos(iTime/2.))*k/2.-i,0.,1.);
        i--;
        u/=2.; 
        if(u<1.)break;

        uv.x+=a(uv.y,u)/(u*2.)*clamp((1.-cos(iTime/2.))*k/2.-i,0.,1.);
        i--;
        u/=2.;
        if(u<1.)break;
    }

    vec2 z=floor(uv);

    vec3 col = vec3(rand(z),0,0);
    col.y=rand(z+col.x);
    col.z=rand(z+col.y);

	col*=1./(1.+2.*length(fract(uv)-0.5));
    //col*=clamp(0.2/length(fract(uv)-0.5),0.,1.);
    //col*=length(fract(uv)-0.5)*2.;

    fragColor = vec4(col,1.0);
    //fragColor=texture(iChannel0,uv);
}
