void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.x;
    uv = normalize(uv) * tan(asin(length(uv) * 1.5));

    vec2 grid = uv * 10.0 - 0.5;
    vec2 line = 1.0 - abs(fract(grid) - 0.5) / fwidth(grid);
    float color = max(line.x, line.y);

    fragColor = vec4(vec3(color),1);
}
