//fork https://www.shadertoy.com/view/WdBXWV
#define f(a) exp( -10.* pow( length( U -.52*cos(a+vec2(0,33)) ) , 2. ) )
#define time iTime

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y;

    O =   ( .5-.5*cos(-time+min(6.*length(U),6.3)) ) 
        * (    .7* vec4(1,.25,0,0)
            + ( f(.65- time*.1 )+f(1.6 + time*.5)+f(2.8 + time) ) * vec4(.8,.8,.5,0) );
  O *= O;
}
