vec2 func(float t)
{
 	return vec2(
        t * sin(t),
        t * cos(t)
    );
}

vec3 getColor(vec2 st, float iter)
{
    vec3 col = vec3(0.0);
    for (float i = 0.; i < iter; i += 0.25)
    {
        vec2 result = func(mod((iTime * sqrt(length(st)) * 0.01 + i * i) * 1.1, 30.0));

        float dist = length(result - st);
        float res = (dist < i * 0.01) ? 1.0: 0.0;

        float id = floor(mod(i, 3.0));
        vec3 col1 = vec3(1., 0., 0.);
        vec3 col2 = vec3(0., 1., 0.);
        vec3 col3 = vec3(0., 0., 1.);
        col += ((id == 0.) ? col1 : (id == 1.) ? col2 : col3)
            * (res * 0.015 / dist);
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 st = uv;
    st -= vec2(0.5);
    st *= vec2(0.5 * (iResolution.x / iResolution.y), 0.5);

    vec3 col = vec3(0.);

    st *= 55.0;

    col += getColor(st, 350.);
    st /= length(st * sin(iTime * 0.01));
    col += getColor(st, 55.);

    // Output to screen
    fragColor = vec4(col,1.0);
}
