// from https://www.shadertoy.com/view/XdfGDH
float normpdf(in float x, in float sigma)
{
	return 0.39894*exp(-0.5*x*x/(sigma*sigma))/sigma;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
// Calculate Kernel
    // from https://www.shadertoy.com/view/XdfGDH
    const int mSize = 11;
    const int kSize = (mSize-1)/2;
    float kernel[mSize];
    vec3 final_color = vec3(0.0);

    //create the 1-D kernel
    float sigma = 20.0;
    float Z = 0.0;
    for (int j = 0; j <= kSize; ++j) {
        kernel[kSize+j] = kernel[kSize-j] = normpdf(float(j), sigma);
    }

    //get the normalization factor (as the gaussian has been clamped)
    for (int j = 0; j < mSize; ++j) {
        Z += kernel[j];
    }

    //read out the texels
    for (int i=-kSize; i <= kSize; ++i) {
        for (int j=-kSize; j <= kSize; ++j) {
            final_color += kernel[kSize+j]*kernel[kSize+i]*texture(iChannel0, (fragCoord.xy+vec2(float(i),float(j))) / iResolution.xy).rgb;

        }
    }

// Calculate fragColor with kernel
    vec2 uv = fragCoord/iResolution.xy;
    vec4 unblurredColor = texture(iChannel0, uv);

    fragColor = vec4(mix(unblurredColor.rgb, final_color/(Z*Z), unblurredColor.a), 1.0);

    float vignette = smoothstep(1.75, 0.0, length((2.0 * uv) - vec2(1.0)));
    fragColor = mix(vec4(0.0, 0.0, 0.1, 1.0), fragColor, vignette);
}

const float FOVY = 3.14159 * 0.25;
const int RAY_STEPS = 256;

const float FOCAL_LENGTH = 16.0;
const float FOCAL_RANGE = 16.0;


struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;	// Assign every object in scene a unique ID int
};

struct PointLight 
{
    vec3 pos;
    vec3 color;
};



void pR(inout vec2 p, float a)
{
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

vec3 rotatePtY(vec3 pt, float a)
{    
    pR(pt.yz, 0.);
    pR(pt.xz, a);
    pR(pt.xy, 0.);

    return pt;
}

// 2D rotation about the y-axis
vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}


void rayCast(vec3 eye, vec3 ref, vec2 uv, out vec3 dir) {

    // Compute camera axis
    vec3 F = ref - eye;
    vec3 R = normalize(cross(F, vec3(0,1,0)));
    vec3 U = normalize(cross(R, F));

    //
    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / iResolution.y;

    vec3 p = ref + uv.x * H + uv.y * V; 

    dir = normalize(p - eye);
}


// SHAPE SDFs
float sphere(vec3 p, float r, vec3 c) {
	return distance(p, c) - r;
}

float cube(vec3 p, vec3 c) {
    return length(max(abs(p) - c, 0.0));
}

float squarePlane(vec3 p, vec4 n)
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float torus(vec3 p, vec2 t)
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q) - t.y;
}

#define BACK_WALL_SDF 	squarePlane(pos + vec3(0.0, 0.0, 8.6), vec4(0., 0., -1., 1.))
#define BACK_WALL_ID 	1
#define CEILING_SDF 	squarePlane(pos + vec3(0.0, -0.15, 0.0), vec4(0., -1., 0., 1.))
#define CEILING_ID 		2
#define FLOOR_SDF	 	squarePlane(pos + vec3(0.0, -0.15, 0.0), vec4(0., 1., 0., 1.))
#define FLOOR_ID		3
#define RIGHT_WALL_SDF 	squarePlane(pos + vec3(0.0, 0.0, 0.0), vec4(-1., 0., 0., 1.))
#define RIGHT_WALL_ID	4
#define LEFT_WALL_SDF 	squarePlane(pos + vec3(0.0, 0.0, 0.0), vec4(1., 0., 0., 1.))
#define LEFT_WALL_ID	5
#define TALL_CUBE_SDF	cube(rotatePtY(pos + vec3(-0.371, 0.3, 8.), -0.5), vec3(0.28, 0.56, 0.28))
#define TALL_CUBE_ID	6
#define SHORT_CUBE_SDF	cube(rotatePtY(pos + vec3(0.371, 0.59, 8.4), 0.4), vec3(0.25, 0.25, 0.25))
#define SHORT_CUBE_ID	7

void sceneMap3D(vec3 pos, out float t, out int obj, vec3 lightPos) {
    t = BACK_WALL_SDF;
    obj = BACK_WALL_ID;

    float t2;
    if ((t2 = CEILING_SDF) < t) {
    	t = t2;
        obj = CEILING_ID;
    }

    if ((t2 = FLOOR_SDF) < t) {
    	t = t2;
        obj = FLOOR_ID;
    }

    if ((t2 = RIGHT_WALL_SDF) < t) {
    	t = t2;
        obj = RIGHT_WALL_ID;
    }

    if ((t2 = LEFT_WALL_SDF) < t) {
    	t = t2;
        obj = LEFT_WALL_ID;
    }

    if ((t2 = TALL_CUBE_SDF) < t) {
    	t = t2;
      obj = TALL_CUBE_ID;
    }

    if ((t2 = SHORT_CUBE_SDF) < t) {
    	t = t2;
    	obj = SHORT_CUBE_ID;
    }
}


float sceneMap3D(vec3 pos, vec3 lightPos) {
    float t = BACK_WALL_SDF;

    float t2;
    if ((t2 = CEILING_SDF) < t) {
    	t = t2;
    }

    if ((t2 = FLOOR_SDF) < t) {
    	t = t2;
    }

    if ((t2 = RIGHT_WALL_SDF) < t) {
    	t = t2;
    }

    if ((t2 = LEFT_WALL_SDF) < t) {
    	t = t2;
    }

    if ((t2 = TALL_CUBE_SDF) < t) {
    	t = t2;
    }

    if ((t2 = SHORT_CUBE_SDF) < t) {
    	t = t2;
    }

    return t;
}


vec3 computeNormal(vec3 pos, vec3 lightPos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                            sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                            sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}


void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos) {
    t = 0.001;

    for (int i = 0; i < RAY_STEPS; ++i) {
    	vec3 pos = origin + t * dir;
        float m; // Minimum distance from this point to an object in the scene
        sceneMap3D(pos, m, hitObj, lightPos);
        if (m < 0.01) {
        	return;
        }
        t += m;
    }
	// We hit nothing
    t = -1.0;
    hitObj = -1;
}


vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightVec, vec3 view) {
    float lambert = dot(lightVec, n);
    switch(hitObj) {
        case FLOOR_ID:
        return vec3(1., 1., 1.) * lambert;
        break;
        case CEILING_ID:
        return vec3(1., 1., 1.) * lambert;
        break;
        case BACK_WALL_ID:
        return vec3(1., 1., 1.) * lambert;
        break;
        case RIGHT_WALL_ID:
        return vec3(1., 0., 0.) * lambert;
        break;
        case LEFT_WALL_ID:
        return vec3(0., 1., 0.6) * lambert;
        break;
        case TALL_CUBE_ID:
        return vec3(1., 1., 1.) * lambert;
        break;
        case SHORT_CUBE_ID:
        return vec3(1., 1., 1.) * lambert;
        break;
        case -1:
        return vec3(0., 0., 0.);
        break;
    }
    return vec3(0., 0., 0.);
}


Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos) {
	float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));

    return Intersection(t, surfaceColor, isect, hitObj);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    PointLight light = PointLight(vec3(0., 1.135, -8.8), vec3(0.5,.5,0.5));

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Convert to range [-1,1]
    uv = uv * 2.0 - vec2(1.0, 1.0);

    vec3 eye = vec3(0, 0.5, -12);
    vec3 ref = vec3(0, 0, 0);
    vec3 dir;

    rayCast(eye, ref, uv, dir);
    Intersection isect = sdf3D(dir, eye, light.pos);

    float  distFromCamera  = abs(dot(normalize(ref - eye), (isect.p - eye)));
    float timeVar = sin(iTime) * 1.5;
	float dofBlurAmount = min(1.0, abs(distFromCamera  - FOCAL_LENGTH) / FOCAL_RANGE * timeVar);

    fragColor = vec4(isect.color * step(0.0, isect.t), dofBlurAmount);
}
