float normpdf(in float x, in float sigma)
{
	return 0.39894*exp(-0.5*x*x/(sigma*sigma))/sigma;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    const int mSize = 11;
    const int kSize = (mSize-1)/2;
    float kernel[mSize];
    vec3 final_colour = vec3(0.0);

    float sigma = 7.0;
    float Z = 0.0;
    for (int j = 0; j <= kSize; ++j)
    {
        kernel[kSize+j] = kernel[kSize-j] = normpdf(float(j), sigma);
    }

    for (int j = 0; j < mSize; ++j)
    {
        Z += kernel[j];
    }

    for (int i=-kSize; i <= kSize; ++i)
    {
        for (int j=-kSize; j <= kSize; ++j)
        {
            final_colour += kernel[kSize+j]*kernel[kSize+i]*texture(iChannel0, (fragCoord.xy+vec2(float(i),float(j))) / iResolution.xy).rgb;

        }
    }


    vec3 col = vec3(0,0,0);

    float mx = iMouse.x;
    float my = iMouse.y;

    if (iMouse.z < 1.0)
    {
        mx = iResolution.x * 0.5;
        my = iResolution.y * 0.5;
    }

    vec2 mXY = vec2( mx, my );


    float radius = 20.0;
	float fDistToPointB = length( mXY - fragCoord.xy) - radius;
    vec3 vColour = vec3(0.0);
	vColour = mix(vColour, vec3(0.0, 0.0, 1.0), (1.0 - clamp(fDistToPointB, 0.0, 1.0)));


	fragColor = vec4(final_colour/(Z*Z) + vColour, 1.0); 

}
