void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    fragColor =  vec4(0);
    vec2 uv = fragCoord.xy / iResolution.xy;

    float f[32];
        f[0] = 0.;
        f[1] = 0.;
        f[2] = 1.;
        f[3] = 0.;
        f[4] = 0.;
        f[5] = 0.;
        f[6] = 0.;
        f[7] = 0.;
        f[8] = 0.;
        f[9] = 0.;
        f[10] = 0.;
        f[11] = 0.;
        f[12] = 1.;
        f[13] = 0.;
        f[14] = 1.;
        f[15] = 0.;
        f[16] = 0.;
        f[17] = 0.;
        f[18] = 0.;
        f[19] = 0.;
        f[20] = 0.;
        f[21] = 1.;
        f[22] = 0.;
        f[23] = 0.;
        f[24] = 0.;
        f[25] = 1.;
        f[26] = 0.;
        f[27] = 0.;
        f[28] = 0.;
        f[29] = 0.;
        f[30] = 0.;
        f[31] = 1.;

    float row = floor(uv.x*32.),
        y = 2.*fract(uv.x*32.),
        pct = f[int(row)];

    float slider = (abs(fract(iTime/9.)*2.-1.) * 32.)-2.; 

    if (uv.y < 0.75 && uv.y > 0.25)
    {   

        fragColor += vec4(smoothstep(1.5*fwidth(y-pct),0., abs(y-.5*pct)) ); //fract(sin(u_time+1.)*10.)
        fragColor += vec4(step(0.9,pct));
        fragColor += vec4(step(1.,row-slider)*0.5-step(1.,row-slider - 1.)*0.5);
        // 
    }
}
