#define v vec2

// circle draw
#define j(x, r) length(x - g) < r

// ring draw, x is pos, r and q are inner and outer radii
#define k(x, r, q) j(x, q) != j(x, r)

// line draw, b is direction
#define l(b) length(g - b) < 1. && length(g - dot(g, b) * b) < (length(g) < S ? A*S : A/S)

float C = .86, // cos(210 deg)
    S = .5, // sin(210 deg)
    F = .3,
    E = .22,
    A = .02,
    G = .21;

void mainImage(out vec4 o, in v p)
{
    v r = iResolution.xy;
    // resolution scaling, g is fragment pos in gl coord [-1, 1]
    v g = (p/S - r)/r.y;

    // black border rings
    o.g = (k(v(0, F), G, G - A) ||
    	k(v(F*C, -F*S), G, G - A) ||
    	k(v(-F*C, -F*S), G, G - A)) ?
        o.g = 0. :
    // white ring
    k(v(0), F, G) ?
        C :
    // black lines and circles
    l(v(0, 1)) ||
        l(v(C, -S)) ||
        l(v(-C, -S)) ||
        j(v(0), .06) ||
        j(v(0, F), G) ||
        j(v(F*C, -F*S), G) ||
        j(v(-F*C, -F*S), G) ?
        0. :
    // white base circles
    j(v(0,E), F) ||
        j(v(E*C,-E*S), F) ||
        j(v(-E*C,-E*S), F) ?
         C : 0.;
}
