float metaball(vec2 p, vec2 pixel, float r)
{
    vec2 d = p-pixel;
	return r / dot(d,d);
}
float metaDiamond(vec2 p, vec2 pixel, float r)
{
    vec2 d = abs(p-pixel);
	return r / (d.x + d.y);
}
float metaEllipse(vec2 p, vec2 pixel, float r, float xM, float yM)
{
    vec2 d = p-pixel;
    d.x /= xM;
    d.y /= yM;
	return r / dot(d,d);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	float r = metaball(iResolution.xy / 2., fragCoord, 200.);

	float r1 = metaDiamond(iResolution.xy / 2. + vec2(sin(iTime) * 200.,  0.), fragCoord, 20.);

	float r2 = metaEllipse(iResolution.xy / 2. + vec2(0., cos(iTime) * 200.), fragCoord, 50., 1., 5.);

    fragColor = vec4(vec3(r+r1+r2), 1.0);
}
