void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 cval = texture(iChannel0,fragCoord / iResolution.xy * 1.0);

    vec4 mapped = texture(iChannel1, cval.zy)*0.33 +
                  texture(iChannel1, cval.xy)*0.33 +
        		  texture(iChannel3, cval.xw)*0.33;

    fragColor = mapped*cval.w;
}

vec3 read(vec2 fragCoord, vec2 pos)
{
    return texture(iChannel0,(fragCoord+pos)/iResolution.xy).xyz;
}

vec4 read4(vec2 fragCoord, vec2 pos)
{
    return texture(iChannel0,(fragCoord+pos)/iResolution.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor=texture(iChannel0, fragCoord/iResolution.xy); 

    if(iFrame==0 || mod(float(iFrame),8000.0) ==0.0)
    {
        //fragColor=texture(iChannel1, fragCoord/iResolution.xy); 

       fragColor = vec4(0.0);
    }
    else
    {
        vec4 outp = read4(fragCoord, vec2(0.0,0.0));

        float ref = outp.w;

		vec4 u = vec4(0.0);//read(fragCoord, vec2(0.0,0.0));
    	u+=(read4(fragCoord, vec2(1.0,0.0)));
        u+=(read4(fragCoord, vec2(-1.0,0.0)));
        u+=(read4(fragCoord, vec2(0.0,1.0)));
        u+=(read4(fragCoord, vec2(0.0,-1.0)));
        u+=(read4(fragCoord, vec2(1.0,1.0)));
        u+=(read4(fragCoord, vec2(-1.0,-1.0)));
        u+=(read4(fragCoord, vec2(-1.0,1.0)));
        u+=(read4(fragCoord, vec2(1.0,-1.0)));
        u/=8.0;


        float p1 = cos(iTime)*0.5+0.5;
        float p2 = sin(iTime*0.5)*0.5+0.5;
        float p3 = cos(iTime*0.75)*0.5+0.5;


        outp.x = cos(u.x)*u.w*1.2;//u.x*(u.z-u.y)*ref;
        outp.y = u.y + p1*u.y*(u.x-u.z)*u.w;
        outp.z = u.z + p2*u.z*(u.y-u.x)*u.w;
		outp.w = u.w + u.x*0.44 - u.y*0.15;;//p3*u.w*(u.w-u.x)*u.w;

        //outp.w = 0.0;//outp.x*outp.z*outp.y;

        if(length(fragCoord - vec2(iResolution.x*0.5,iResolution.y*0.5))<50.0 + cos(iTime)*50.0 )
            outp = vec4(1.0);

        fragColor = vec4(clamp(outp, 0.0, 1.0));
    }
}
