#define R iResolution.xy
#define PI 3.1415926535897932384626433832795
#define TPW 3./R.y
float circle(vec2 uv, vec2 ctr, float sz) {
	float pct = 0.;
    pct = distance(uv,ctr);
    pct = smoothstep(sz+TPW,sz,pct);
    return pct;
}
float polygon(vec2 st,vec2 pos,int n,float sz) {
    vec2 uv = st*2.-pos/sz;
	float a=atan(uv.x,uv.y)+PI;
    float b=2.*PI/float(n);
    return smoothstep(.5+TPW,.5,
      cos(floor(.5+a/b)*b-a)*length(uv)+.5-sz);
}
void mainImage( out vec4 O, in vec2 U )
{
   	vec2 uv=(2.*U.xy-R)/R.y;
	float t = 0.;      
    t = max(t,polygon(uv,vec2(-0.040,-0.060),2,0.4));
    t -= min(t,polygon(uv,vec2(0.010,1.000),4,1.088));
    t -= min(t,polygon(uv,vec2(-0.85,-0.37),4,0.528));
    t += max(t,circle(uv,vec2(-0.710,0.920),0.336));
    t -= min(t,polygon(uv,vec2(-0.210,0.740),4,0.800));
    t += max(t,polygon(uv,vec2(0.050,0.230),4,0.328));
    t -= min(t,circle(uv,vec2(0.220,0.470),0.120));
    t += max(t,polygon(uv,vec2(-0.,-0.65),3,0.392));
    O = vec4(vec3(t),1.0);
}
