#define PI 3.141592653
#define PI2 2.0*PI
#define EXP2PII(t) vec2(cos(PI2*t), sin(PI2*t))

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    const float speed = 1.0;
    float time = speed * iTime + 100.0;

	vec2 uv = fragCoord/iResolution.xy;

    vec4 color = texture(iChannel0, uv);

    vec2 offset = vec2(0.0);

    const int m = 32;
    for (int n = 0; n < m; n++)
    {
		vec2 testOffset = 0.1 * EXP2PII(float(n) / float(m)) / float(m);
        vec4 testColor = color*texture(iChannel0, uv+testOffset);

        float l = sqrt(1.0/3.0)*length(testColor);

        offset += l * testOffset;
    }

    vec2 warp = vec2(sin(0.7*time+9.0*uv.x+4.0), cos(time+16.0*uv.y));
    vec2 uvOffset = warp * offset - 0.01*warp;

	fragColor = texture(iChannel0, uv+uvOffset);
}
