void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 a = fragCoord/iResolution.x;
    vec2 b = fragCoord/iResolution.y;
    // iMouse get position of mouse , move mouse into are of shader
    //float sin_dot = sin(dot(1.0/a*iMouse.x,1.0/b*iMouse.y));
    float sin_dot = sin(dot(a*iMouse.x,b*iMouse.y));
    fragColor = vec4(sin_dot);
}
