void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color

    vec4 col = vec4(0.00,1.00,1.00,1.00);
    float progress = fragCoord.x + (iTime*200.00);
    float scale = 10.00;

    if(iMouse.y>5.00){scale=iMouse.y;}

    float groundHeighta = (((sin(progress/50.00))+2.00)*iResolution.y)/scale;
    float groundHeightb = (((sin(progress/70.00))+2.00)*iResolution.y)/scale;
    float groundHeightc = (((sin(progress/130.00))+2.00)*iResolution.y)/scale;
    float groundHeightd = (((sin(progress/23.00))+2.00)*iResolution.y)/scale;

    float groundHeight = groundHeighta + groundHeightb + groundHeightc + groundHeightd;

    //if(fragCoord.y<groundHeight) {
        //col = texture(iChannel0, vec2(progress/512.00,fragCoord.y/groundHeight));
        //col = col+texture(iChannel0, vec2(uv.x,fragCoord.y/groundHeight));
    //} else {
        //col = texture(iChannel0, vec2(uv.x,fragCoord.y/groundHeight));
    //}

    col = texture(iChannel0, vec2(progress/512.00,fragCoord.y/groundHeight));
    col = col+texture(iChannel0, vec2(uv.x,fragCoord.y/groundHeight));

    // Output to screen
    fragColor = vec4(col);
}
