//these two floats set the amplitude(amp) and frequency(frq) of the zoom
float frq = 1.0;
float amp = 2.0;

//this determines where on screen it zooms in on, (.5,.5) is center
vec2 center = vec2(.5,.5);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// Normalized pixel coordinates (from 0 to 1)
	vec2 uv = fragCoord/iResolution.xy;

    //creates a shorthand for sin(iTime)
    float sSin = (sin(iTime*frq)*amp)+(amp+1.0);

    //changes the uv to zoom in on the screen
	uv = center + (uv-center)/sSin;

    vec4 col = texture(iChannel0,uv);

	// Output to screen
	fragColor = col;
}
