void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates for y (from -1 to 1), for x (from -xMin to xMax)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // rotate 45 degrees
    uv = vec2(uv.x*.5+uv.y*.5, uv.x*.5-uv.y*.5);
    // tile more jellys
    uv *= 5.;

    float idx = trunc(uv.x) + 2.*trunc(uv.y);
    // each jelly is in range 0-1, 0-1
    uv = fract(uv);

    // bg
    vec3 col = vec3(uv.xy*.8, .9).yxz;

    // jelly belly
    float d = length(uv - vec2(.5))*2.;
    float rmix = .5*sin(iTime+length(uv)*8.+idx*8.)+.5; 
    float r = mix(.4,.7,rmix);
    float sr = 0.9;
    float jelly = smoothstep(r, sr*r,d)*0.3;
    col += jelly;

    // tentacles
    if (jelly < .2) {
        if (abs(uv.x - uv.y + sin(uv.x*18.+uv.y*18. + iTime)*0.05) < 0.008) {
            col += smoothstep(.2,.3, uv.x+uv.y)*0.6;
            col -= smoothstep(.8,.9, uv.x+uv.y)*0.6;
        }    
        else if (abs(uv.x - uv.y - .2 + sin(uv.x*18.+uv.y*18. + iTime*2.)*0.05) < 0.008) {
            col += smoothstep(.3,.4, uv.x+uv.y)*0.6;
            col -= smoothstep(.8,.9, uv.x+uv.y)*0.6;
        }    
        else if (abs(uv.x - uv.y + .2 + sin(uv.x*18.+uv.y*18. + iTime*2.)*0.05) < 0.008) {
            col += smoothstep(.3,.4, uv.x+uv.y)*0.6;
            col -= smoothstep(.8,.9, uv.x+uv.y)*0.6;
        }
    }

    fragColor = vec4(col, 1);
}
